mapper.select:
        select * from kjet_child_apply
        WHERE isdel = 1 and village_status != 1
         
        	
	            and child_city_id=#{orgId}
	        
	         
	            and child_county_id=#{orgId}
	        
	         
	            and town_id=#{orgId}
	        
	         
	            and village_id=#{orgId}
	        
	        and city_status != 1
        
         
       		
            	and child_city_id=#{orgId}
	        
	         
	            and child_county_id=#{orgId}
	        
	         
	            and town_id=#{orgId}
	        
	         
	            and village_id=#{orgId}
	        
	        and county_status != 1
        
         
          	
            	and child_city_id=#{orgId}
	        
	         
	            and child_county_id=#{orgId}
	        
	         
	            and town_id=#{orgId}
	        
	         
	            and village_id=#{orgId}
	        
	        and town_status != 1
